// Profile.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Profile.scss';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({ email: '', username: '' });
  const [passwords, setPasswords] = useState({ old_password: '', new_password: '', confirm_password: '' });
  const [projects, setProjects] = useState([]);
  const { userId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserDetailsAndProjects = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const userDetailsResponse = await axios.get(`http://localhost:5000/api/user/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserDetails(userDetailsResponse.data);

        const projectsResponse = await axios.get(`http://localhost:5000/api/user/${userId}/projects`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProjects(projectsResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchUserDetailsAndProjects();
  }, [userId, navigate]);

  const handleUserDetailsChange = e => {
    const { name, value } = e.target;
    setUserDetails(prev => ({ ...prev, [name]: value }));
  };

  const handleUpdateUserDetails = async e => {
    e.preventDefault();
    const token = localStorage.getItem('access_token');
    try {
      await axios.put(`http://localhost:5000/api/user/${userId}`, userDetails, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' },
      });
      alert('User details updated successfully.');
    } catch (error) {
      alert('Failed to update user details.');
      console.error(error);
    }
  };

  const handlePasswordChange = e => {
    const { name, value } = e.target;
    setPasswords(prev => ({ ...prev, [name]: value }));
  };

  const handleUpdatePassword = async e => {
    e.preventDefault();
    if (passwords.new_password !== passwords.confirm_password) {
      alert('Passwords do not match.');
      return;
    }

    const token = localStorage.getItem('access_token');
    try {
      await axios.put(`http://localhost:5000/api/user/${userId}/change-password`, {
        old_password: passwords.old_password,
        new_password: passwords.new_password,
      }, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' },
      });
      alert('Password updated successfully.');
      setPasswords({ old_password: '', new_password: '', confirm_password: '' });
    } catch (error) {
      alert('Failed to update password.');
      console.error(error);
    }
  };

  // Handling project status change and unassignment not included in this snippet
  // Ensure to implement those as per your application's requirements

  return (
    <div className="profile-container">
      <h1>Profile</h1>
      {/* User details and password update forms */}
      <div className="projects-container">
        <h2>Your Projects</h2>
        {projects.length > 0 ? (
          projects.map((project) => (
            <div key={project.id} className="project-item">
              <h3>{project.title}</h3>
              <p>Description: {project.description}</p>
              <p>Status: {project.status}</p>
              {/* Project status change and unassign buttons */}
            </div>
          ))
        ) : (
          <p>No projects found.</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
