// Projects.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Projects.scss'; // Assuming this file exists and is correctly structured

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState({ title: '', description: '', status: 'open' });

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const { data } = await axios.get('http://localhost:5000/api/projects', {
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
        });
        setProjects(data);
      } catch (error) {
        console.error("Failed to fetch projects", error);
      }
    };
    fetchProjects();
  }, []);

  const handleAddProject = async () => {
    try {
      await axios.post('http://localhost:5000/api/projects', newProject, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      setNewProject({ title: '', description: '', status: 'open' }); // Reset form after submission
      fetchProjects(); // Re-fetch projects to update the list
    } catch (error) {
      console.error("Failed to add project", error);
    }
  };

  const handleStatusChange = async (projectId, status) => {
    try {
      await axios.put(`http://localhost:5000/api/projects/${projectId}`, { status }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      fetchProjects(); // Re-fetch projects to update the list
    } catch (error) {
      console.error("Failed to update project status", error);
    }
  };

  return (
    <div className="projects-container">
      <h1>Projects</h1>
      <div className="project-inputs">
        <input
          type="text"
          placeholder="Title"
          value={newProject.title}
          onChange={(e) => setNewProject({ ...newProject, title: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newProject.description}
          onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
        />
        <button onClick={handleAddProject}>Add Project</button>
      </div>
      {projects.map((project) => (
        <div key={project.id} className="project-item">
          <h2>{project.title}</h2>
          <p>{project.description}</p>
          <p>Status: {project.status}</p>
          <button onClick={() => handleStatusChange(project.id, 'in progress')}>In Progress</button>
          <button onClick={() => handleStatusChange(project.id, 'done')}>Done</button>
        </div>
      ))}
    </div>
  );
};

export default Projects;
