// Profile.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Profile.scss';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({ email: '', username: '' });
  const [passwords, setPasswords] = useState({ old_password: '', new_password: '', confirm_password: '' });
  const [projects, setProjects] = useState([]);
  const { userId } = useParams(); // Extract userId from the URL
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserDetailsAndProjects = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        // Fetch user details
        const userDetailsResponse = await fetch(`http://localhost:5000/api/user/${userId}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!userDetailsResponse.ok) {
          throw new Error('Could not fetch user details');
        }

        const userDetailsData = await userDetailsResponse.json();
        setUserDetails(userDetailsData);

        // Fetch user projects
        const projectsResponse = await fetch(`http://localhost:5000/api/user/${userId}/projects`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!projectsResponse.ok) {
          throw new Error('Could not fetch user projects');
        }

        const projectsData = await projectsResponse.json();
        setProjects(projectsData);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchUserDetailsAndProjects();
  }, [navigate, userId]);

  const handleUserDetailsChange = (e) => {
    const { name, value } = e.target;
    setUserDetails(prevState => ({ ...prevState, [name]: value }));
  };

  const handleUpdateUserDetails = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('access_token');
    try {
      const response = await fetch(`http://localhost:5000/api/user/${userId}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userDetails),
      });

      if (!response.ok) {
        throw new Error('Could not update user details');
      }

      alert('User details updated successfully');
    } catch (error) {
      console.error("Error updating user details:", error);
    }
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswords(prevState => ({ ...prevState, [name]: value }));
  };

  const handleUpdatePassword = async (e) => {
    e.preventDefault();
    if (passwords.new_password !== passwords.confirm_password) {
      alert("Passwords do not match.");
      return;
    }

    const token = localStorage.getItem('access_token');
    try {
      const response = await fetch(`http://localhost:5000/api/user/${userId}/change-password`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          old_password: passwords.old_password, 
          new_password: passwords.new_password
        }),
      });

      if (!response.ok) {
        throw new Error('Could not update password');
      }

      alert('Password updated successfully');
      setPasswords({ old_password: '', new_password: '', confirm_password: '' });
    } catch (error) {
      console.error("Error updating password:", error);
    }
  };

  return (
    <div className="profile-container">
      <h1>Profile</h1>
      <form onSubmit={handleUpdateUserDetails} className="profile-form">
        <h2>Update User Details</h2>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" name="email" value={userDetails.email} onChange={handleUserDetailsChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Username:</label>
          <input type="text" name="username" value={userDetails.username} onChange={handleUserDetailsChange} className="form-control" />
        </div>
        <button type="submit" className="btn">Update Details</button>
      </form>

      <form onSubmit={handleUpdatePassword} className="profile-form">
        <h2>Change Password</h2>
        <div className="form-group">
          <label>Old Password:</label>
          <input type="password" name="old_password" value={passwords.old_password} onChange={handlePasswordChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>New Password:</label>
          <input type="password" name="new_password" value={passwords.new_password} onChange={handlePasswordChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Confirm New Password:</label>
          <input type="password" name="confirm_password" value={passwords.confirm_password} onChange={handlePasswordChange} className="form-control" />
        </div>
        <button type="submit" className="btn">Update Password</button>
      </form>

      <div className="projects-container">
        <h2>Your Projects</h2>
        {projects.length > 0 ? (
          projects.map((project) => (
            <div key={project.id} className="project-item">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              {/* More project details can be added here */}
            </div>
          ))
        ) : (
          <p>No projects found.</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
