// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.scss';

const Login = () => {
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Here you would implement your login logic, possibly sending a request to your backend.
    // For now, we'll just simulate a login.
    console.log('Login attempt with:', credentials);
    // If login is successful:
    setIsLoggedIn(true);
    navigate('/profile'); // Redirect to the profile page or wherever you need
  };

  // If the user is logged in, redirect to the profile or dashboard page
  if (isLoggedIn) {
    navigate('/profile'); // Adjust the route as needed
  }

  return (
    <div className="login-container">
      <form onSubmit={handleSubmit} className="login-form">
        <h2>Login</h2>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={credentials.username}
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={credentials.password}
          onChange={handleChange}
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
