// App.js
import React, { useState } from 'react';
import Navbar from './components/Navbar/Navbar';
import Sidebar from './components/Sidebar/Sidebar';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login/Login';
import Register from './components/Register/Register';
import Profile from './components/Profile/Profile';
import Projects from './components/Projects/Projects';
import Issues from './components/Issues/Issues';
import './App.scss';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('access_token'));

  const handleLogin = () => {
    setIsAuthenticated(true);
    // Simulate setting user ID upon login; in a real app, this would come from the login response
    localStorage.setItem('userId', '2'); // Example user ID
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('userId');
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />
      <div className="app-body">
        <Sidebar />
        <div className="content">
          <Routes>
            <Route path="/login" element={!isAuthenticated ? <Login onLogin={handleLogin} /> : <Navigate to={`/profile/${localStorage.getItem('userId')}`} replace />} />
            <Route path="/register" element={!isAuthenticated ? <Register /> : <Navigate to={`/profile/${localStorage.getItem('userId')}`} replace />} />
            <Route path="/profile/:userId" element={isAuthenticated ? <Profile /> : <Navigate to="/login" replace />} />
            <Route path="/projects" element={isAuthenticated ? <Projects /> : <Navigate to="/login" replace />} />
            <Route path="/issues" element={isAuthenticated ? <Issues /> : <Navigate to="/login" replace />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
