//Profile.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserDetails = async () => {
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/login'); // Redirect to login if no token found
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/user', {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error('Could not fetch user details');
        }

        const data = await response.json();
        setUserDetails(data);
      } catch (error) {
        console.error("Error fetching user details:", error);
      }
    };

    fetchUserDetails();
  }, [navigate]);

  // Add form or UI elements to display userDetails and allow updates
  // Display assigned projects and issues similarly by fetching from respective backend routes

  return (
    <div>
      <h1>Profile</h1>
      <p>Username: {userDetails.username}</p>
      <p>Email: {userDetails.email}</p>
      {/* Render form to update user details */}
      {/* List assigned projects and issues */}
    </div>
  );
};

export default Profile;
