// Profile.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Profile.scss';

const Profile = () => {
  const [userDetails, setUserDetails] = useState({ email: '', username: '' });
  const [projects, setProjects] = useState([]);
  const [issues, setIssues] = useState([]);
  const navigate = useNavigate();

  // Retrieve the user ID stored in localStorage after successful login
  const userId = localStorage.getItem('user_id');

  useEffect(() => {
    if (!userId) {
      navigate('/login'); // Redirect to login if no user ID is found
    } else {
      fetchUserDetails();
      fetchUserProjects();
      fetchUserIssues();
    }
  }, [userId, navigate]); // Depend on userId and navigate to avoid unnecessary fetches

  const fetchUserDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${userId}`);
      setUserDetails(response.data);
    } catch (error) {
      console.error("Error fetching user details:", error);
    }
  };

  const fetchUserProjects = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${userId}/projects`);
      setProjects(response.data);
    } catch (error) {
      console.error("Error fetching user projects:", error);
    }
  };

  const fetchUserIssues = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${userId}/issues`);
      setIssues(response.data);
    } catch (error) {
      console.error("Error fetching user issues:", error);
    }
  };

  const unassignIssue = async (issueId) => {
    try {
      await axios.put(`http://localhost:5000/api/issues/${issueId}/unassign`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      await fetchUserIssues();
      alert('Issue unassigned successfully');
    } catch (error) {
      console.error("Error unassigning issue:", error);
      alert('Failed to unassign issue');
    }
  };

  const handleUserDetailsChange = (e) => {
    const { name, value } = e.target;
    setUserDetails(prevState => ({ ...prevState, [name]: value }));
  };

  const handleUpdateUserDetails = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/user/${userId}`, userDetails, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      alert('User details updated successfully');
    } catch (error) {
      console.error("Error updating user details:", error);
      alert('Failed to update user details');
    }
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswords(prevState => ({ ...prevState, [name]: value }));
  };

  const handleUpdatePassword = async (e) => {
    e.preventDefault();
    if (passwords.new_password !== passwords.confirm_password) {
      alert("Passwords do not match.");
      return;
    }
    try {
      await axios.put(`http://localhost:5000/api/user/${userId}/change-password`, {
        old_password: passwords.old_password,
        new_password: passwords.new_password,
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      alert('Password updated successfully');
      setPasswords({ old_password: '', new_password: '', confirm_password: '' });
    } catch (error) {
      console.error("Error updating password:", error);
      alert('Failed to update password');
    }
  };

  const unassignProject = async (projectId) => {
    try {
      await axios.put(`http://localhost:5000/api/projects/${projectId}/unassign`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      fetchUserProjects();
      alert('Project unassigned successfully');
    } catch (error) {
      console.error("Error unassigning project:", error);
      alert('Failed to unassign project');
    }
  };

  return (
    <div className="profile-container">
      <h1>Profile</h1>
      <form onSubmit={handleUpdateUserDetails} className="profile-form">
        <h2>Update User Details</h2>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" name="email" value={userDetails.email} onChange={handleUserDetailsChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Username:</label>
          <input type="text" name="username" value={userDetails.username} onChange={handleUserDetailsChange} className="form-control" />
        </div>
        <button type="submit" className="btn">Update Details</button>
      </form>

      <form onSubmit={handleUpdatePassword} className="profile-form">
        <h2>Change Password</h2>
        <div className="form-group">
          <label>Old Password:</label>
          <input type="password" name="old_password" value={passwords.old_password} onChange={handlePasswordChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>New Password:</label>
          <input type="password" name="new_password" value={passwords.new_password} onChange={handlePasswordChange} className="form-control" />
        </div>
        <div className="form-group">
          <label>Confirm New Password:</label>
          <input type="password" name="confirm_password" value={passwords.confirm_password} onChange={handlePasswordChange} className="form-control" />
        </div>
        <button type="submit" className="btn">Update Password</button>
      </form>

      <div className="projects-container">
        <h2>Your Projects</h2>
        {projects.length > 0 ? projects.map(project => (
          <div key={project.id} className="project-item">
            <h3>{project.title}</h3>
            <p>{project.description}</p>
            <p>Status: {project.status}</p>
            <button onClick={() => unassignProject(project.id)}>Unassign Project</button>
          </div>
        )) : <p>No projects found.</p>}
      </div>

      {/* New section for issues */}
      <div className="issues-container">
        <h2>Your Issues</h2>
        {issues.length > 0 ? issues.map(issue => (
          <div key={issue.id} className="issue-item">
            <h3>{issue.title}</h3>
            <p>Description: {issue.description}</p>
            <p>Status: {issue.status}</p>
            {/* Replace project ID with project title if you have that data available */}
            <p>Project: {issue.projectId}</p>
            <button onClick={() => unassignIssue(issue.id)}>Unassign Issue</button>
          </div>
        )) : <p>No issues assigned.</p>}
      </div>
    </div>
  );
};

export default Profile;
