//Issues.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Issues.scss'; // Make sure this path is correct for your CSS

const Issues = () => {
  const [issues, setIssues] = useState([]);
  const [newIssue, setNewIssue] = useState({
    title: '',
    description: '',
    projectId: '',
    status: 'open',
  });
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    fetchProjects();
    fetchIssues();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/projects');
      setProjects(response.data);
    } catch (error) {
      console.error("Failed to fetch projects", error);
    }
  };

  const fetchIssues = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/issues');
      setIssues(response.data);
    } catch (error) {
      console.error("Failed to fetch issues", error);
    }
  };

  const handleAddIssue = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/issues', {
        title: newIssue.title,
        description: newIssue.description,
        status: newIssue.status,
        projectId: newIssue.projectId  // This matches the key expected by the backend
      });
      console.log('Issue added:', response.data);
      fetchIssues();
      setNewIssue({ title: '', description: '', projectId: '', status: 'open' }); // Reset form
    } catch (error) {
      console.error("Failed to add issue", error);
    }
  };

  const handleStatusChange = async (issueId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/issues/${issueId}`, { status: newStatus });
      fetchIssues();
    } catch (error) {
      console.error("Failed to update issue status", error);
    }
  };

  return (
    <div className="issues-container">
      <h1>Issues</h1>
      <div className="issue-form">
        <input
          type="text"
          placeholder="Title"
          value={newIssue.title}
          onChange={(e) => setNewIssue({ ...newIssue, title: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newIssue.description}
          onChange={(e) => setNewIssue({ ...newIssue, description: e.target.value })}
        />
        <select
          value={newIssue.projectId}
          onChange={(e) => setNewIssue({ ...newIssue, projectId: e.target.value })}
        >
          <option value="">Select Project</option>
          {projects.map((project) => (
            <option key={project.id} value={project.id}>{project.title}</option>
          ))}
        </select>
        <select
          value={newIssue.status}
          onChange={(e) => setNewIssue({ ...newIssue, status: e.target.value })}
          >
            <option value="open">Open</option>
            <option value="in progress">In Progress</option>
            <option value="done">Done</option>
          </select>
          <button onClick={handleAddIssue}>Add Issue</button>
        </div>
        {issues.map((issue) => (
          <div key={issue.id} className="issue-item">
            <h2>{issue.title}</h2>
            <p>{issue.description}</p>
            <p>Status: <select value={issue.status} onChange={(e) => handleStatusChange(issue.id, e.target.value)}>
              <option value="open">Open</option>
              <option value="in progress">In Progress</option>
              <option value="done">Done</option>
            </select></p>
            <p>Project ID: {issue.projectId}</p>
          </div>
        ))}
      </div>
    );
  };
  
  export default Issues;
