//Issues.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Issues.scss'; // Ensure this path matches your file structure

const Issues = () => {
  const [issues, setIssues] = useState([]);
  const [newIssue, setNewIssue] = useState({
    title: '',
    description: '',
    projectId: '',
    status: 'open',
  });
  const [projects, setProjects] = useState([]);

  // Retrieve the user ID from localStorage to assign issues to this user
  const userId = localStorage.getItem('user_id');

  useEffect(() => {
    fetchProjects();
    fetchIssues();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/projects');
      setProjects(response.data);
    } catch (error) {
      console.error("Failed to fetch projects", error);
    }
  };

  const fetchIssues = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/issues');
      setIssues(response.data);
    } catch (error) {
      console.error("Failed to fetch issues", error);
    }
  };

  const handleAddIssue = async () => {
    try {
      // Include the user_id to assign the issue to the current user
      const response = await axios.post('http://localhost:5000/api/issues', {
        ...newIssue,
        user_id: userId, // Assuming your backend handles this field
      });
      console.log('Issue added:', response.data);
      fetchIssues(); // Refresh the list of issues
      setNewIssue({ title: '', description: '', projectId: '', status: 'open' }); // Reset the form
    } catch (error) {
      console.error("Failed to add issue", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewIssue(prev => ({ ...prev, [name]: value }));
  };

  const handleStatusChange = async (issueId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/issues/${issueId}`, { status: newStatus });
      fetchIssues(); // Refresh the list to show the updated status
    } catch (error) {
      console.error("Failed to update issue status", error);
    }
  };

  return (
    <div className="issues-container">
      <h1>Issues</h1>
      {/* Issue creation form */}
      <div className="issue-form">
        <input
          type="text"
          placeholder="Title"
          value={newIssue.title}
          onChange={handleInputChange}
          name="title"
        />
        <textarea
          placeholder="Description"
          value={newIssue.description}
          onChange={handleInputChange}
          name="description"
        />
        <select
          value={newIssue.projectId}
          onChange={handleInputChange}
          name="projectId"
        >
          <option value="">Select Project</option>
          {projects.map((project) => (
            <option key={project.id} value={project.id}>{project.title}</option>
          ))}
        </select>
        <select
          value={newIssue.status}
          onChange={handleInputChange}
          name="status"
        >
          <option value="open">Open</option>
          <option value="in progress">In Progress</option>
          <option value="done">Done</option>
        </select>
        <button onClick={handleAddIssue}>Add Issue</button>
      </div>
      {/* Displaying issues */}
      {issues.map((issue) => (
        <div key={issue.id} className="issue-item">
          <h2>{issue.title}</h2>
          <p>Description: {issue.description}</p>
          <p>Status: 
            <select 
              value={issue.status} 
              onChange={(e) => handleStatusChange(issue.id, e.target.value)}
            >
              <option value="open">Open</option>
              <option value="in progress">In Progress</option>
              <option value="done">Done</option>
            </select>
          </p>
          {/* Optionally display the assigned user's username instead of just the ID */}
          <p>Assigned to User ID: {issue.user_id || 'None'}</p>
          <p>Project ID: {issue.projectId}</p>
        </div>
      ))}
    </div>
  );
};

export default Issues;
