//Projects.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Projects.scss';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState({ title: '', description: '', status: 'open', user_id: 1 }); // Assuming user_id for simplicity

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    const response = await axios.get('http://localhost:5000/api/projects');
    setProjects(response.data);
  };

  const handleAddProject = async () => {
    await axios.post('http://localhost:5000/api/projects', newProject);
    fetchProjects();
    setNewProject({ title: '', description: '', status: 'open', user_id: 1 }); // Reset form
  };

  const handleStatusChange = async (projectId, status) => {
    await axios.put(`http://localhost:5000/api/projects/${projectId}`, { status });
    fetchProjects();
  };

  return (
    <div className="projects-container">
      <h1>Projects</h1>
      <input type="text" placeholder="Title" value={newProject.title} onChange={(e) => setNewProject({ ...newProject, title: e.target.value })} />
      <textarea placeholder="Description" value={newProject.description} onChange={(e) => setNewProject({ ...newProject, description: e.target.value })} />
      <button onClick={handleAddProject}>Add Project</button>
      {projects.map((project) => (
        <div key={project.id} className="project-item">
          <h2>{project.title}</h2>
          <p>{project.description}</p>
          <p>Status: {project.status}</p>
          <button onClick={() => handleStatusChange(project.id, 'in progress')}>In Progress</button>
          <button onClick={() => handleStatusChange(project.id, 'done')}>Done</button>
        </div>
      ))}
    </div>
  );
};

export default Projects;
