//Issues.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Issues = () => {
  const [issues, setIssues] = useState([]);
  const [newIssue, setNewIssue] = useState({
    title: '',
    description: '',
    projectId: '', // Ensure you have a way to set this, e.g., a select dropdown of projects
    status: 'open'
  });

  useEffect(() => {
    fetchIssues();
  }, []);

  const fetchIssues = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/issues', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('access_token')}`,
        },
      });
      setIssues(data);
    } catch (error) {
      console.error("Failed to fetch issues", error);
    }
  };

  const handleAddIssue = async () => {
    try {
      await axios.post('http://localhost:5000/api/issues', newIssue, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('access_token')}`,
        },
      });
      fetchIssues();
      setNewIssue({ title: '', description: '', projectId: '', status: 'open' }); // Reset form
    } catch (error) {
      console.error("Failed to add issue", error);
    }
  };

  const handleStatusChange = async (issueId, status) => {
    try {
      await axios.put(`http://localhost:5000/api/issues/${issueId}`, { status }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('access_token')}`,
        },
      });
      fetchIssues();
    } catch (error) {
      console.error("Failed to update issue status", error);
    }
  };

  return (
    <div>
      <h1>Issues</h1>
      <input
        type="text"
        placeholder="Title"
        value={newIssue.title}
        onChange={(e) => setNewIssue({ ...newIssue, title: e.target.value })}
      />
      <textarea
        placeholder="Description"
        value={newIssue.description}
        onChange={(e) => setNewIssue({ ...newIssue, description: e.target.value })}
      />
      <input
        type="text"
        placeholder="Project ID"
        value={newIssue.projectId}
        onChange={(e) => setNewIssue({ ...newIssue, projectId: e.target.value })}
      />
      <select
        value={newIssue.status}
        onChange={(e) => setNewIssue({ ...newIssue, status: e.target.value })}
      >
        <option value="open">Open</option>
        <option value="in progress">In Progress</option>
        <option value="done">Done</option>
      </select>
      <button onClick={handleAddIssue}>Add Issue</button>
      {issues.map((issue) => (
        <div key={issue.id}>
          <h2>{issue.title}</h2>
          <p>{issue.description}</p>
          <p>Status: {issue.status}</p>
          <button onClick={() => handleStatusChange(issue.id, 'in progress')}>In Progress</button>
          <button onClick={() => handleStatusChange(issue.id, 'done')}>Done</button>
        </div>
      ))}
    </div>
  );
};

export default Issues;
