// Sidebar.js
import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Sidebar.scss';

const Sidebar = () => {
  const navigate = useNavigate();

  // Assuming you have a context or a method to get the current user's ID
  // For demonstration, let's use localStorage to simulate getting the user ID
  const userId = localStorage.getItem('userId');

  return (
    <div className="sidebar">
      <ul className="sidebar-list">
        {/* Using a function to navigate to dynamically generated path */}
        <li className="sidebar-item" onClick={() => navigate(`/profile/${userId || ''}`)}>Profile</li>
        <li className="sidebar-item">
          <Link to="/projects">Projects</Link>
        </li>
        <li className="sidebar-item">
          <Link to="/issues">Issues</Link>
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
