// Navbar.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.scss';

const Navbar = () => {
  const navigate = useNavigate();

  // Check if user is logged in by checking the localStorage
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

  const handleLogoutClick = () => {
    // Clear user id and login state from localStorage
    localStorage.removeItem('user_id');
    localStorage.setItem('isLoggedIn', 'false');
    
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <nav className="navbar">
      <div className="navbar-title">Project Management App</div>
      <div className="navbar-links">
        {!isLoggedIn ? (
          <>
            <Link to="/login" className="nav-link">Login</Link>
            <Link to="/register" className="nav-link">Register</Link>
          </>
        ) : (
          <>
            {/* Link to navigate to the user's profile using the user_id stored in localStorage */}
            <Link to={`/profile/${localStorage.getItem('user_id')}`} className="nav-link">Profile</Link>
            <button onClick={handleLogoutClick} className="nav-link">Logout</button>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
