// AuthContext.js
import React, { createContext, useContext, useState, useCallback } from 'react';
import axios from 'axios'; // Assuming axios is used for HTTP requests

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('access_token'));
  const [userId, setUserId] = useState(localStorage.getItem('userId'));

  // Realistic login function using axios for API call
  const login = useCallback(async (username, password) => {
    try {
      // Replace URL with your actual login endpoint
      const response = await axios.post('http://yourapi.com/api/login', {
        username,
        password
      });

      if (response.data && response.data.access_token && response.data.userId) {
        // Assuming the response includes an access token and a user ID
        localStorage.setItem('access_token', response.data.access_token);
        localStorage.setItem('userId', response.data.userId.toString()); // Ensure userId is stored as a string
        setIsAuthenticated(true);
        setUserId(response.data.userId);
        return response.data.userId; // Return the user ID for further actions
      } else {
        // Handle login failure
        setIsAuthenticated(false);
        setUserId(null);
        console.error("Login failed: No access token or user ID returned");
        return null;
      }
    } catch (error) {
      console.error("Login error:", error);
      setIsAuthenticated(false);
      setUserId(null);
      return null;
    }
  }, []);

  const logout = useCallback(() => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('userId');
    setIsAuthenticated(false);
    setUserId(null);
  }, []);

  return (
    <AuthContext.Provider value={{ isAuthenticated, userId, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
