//Projects.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Projects.scss';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState({
    title: '',
    description: '',
    status: 'open',
  });
  const userId = localStorage.getItem('user_id'); // Ensure this matches how you store user ID

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/projects/user/' + userId); // Adjusted to call a hypothetical endpoint that fetches projects with user-specific data
      setProjects(response.data);
    } catch (error) {
      console.error("Failed to fetch projects", error);
    }
  };

  const handleAddProject = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/projects', { ...newProject });
      console.log('Project added:', response.data);
      fetchProjects(); // Refetch projects to show the newly added one
      setNewProject({ title: '', description: '', status: 'open' }); // Reset the form
    } catch (error) {
      console.error("Failed to add project", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProject(prev => ({ ...prev, [name]: value }));
  };

  const handleAssignProject = async (projectId) => {
    try {
      await axios.put(`http://localhost:5000/api/projects/${projectId}/assign`, { userId });
      fetchProjects();
    } catch (error) {
      console.error("Failed to assign project", error);
    }
  };

  const handleStatusChange = async (projectId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/projects/${projectId}/status`, { status: newStatus, userId }); // This assumes your backend can handle status updates with userId
      fetchProjects();
    } catch (error) {
      console.error("Failed to update project status", error);
    }
  };

  return (
    <div className="projects-container">
      <h1>Projects</h1>
      <div className="add-project-form">
        <input type="text" placeholder="Title" value={newProject.title} onChange={handleInputChange} name="title" />
        <textarea placeholder="Description" value={newProject.description} onChange={handleInputChange} name="description" />
        <select value={newProject.status} onChange={handleInputChange} name="status">
          <option value="open">Open</option>
          <option value="in progress">In Progress</option>
          <option value="done">Done</option>
        </select>
        <button onClick={handleAddProject}>Add Project</button>
      </div>
      {projects.map(project => (
        <div key={project.id} className="project-item">
          <h2>{project.title}</h2>
          <p>Description: {project.description}</p>
          <p>Status: {project.userStatus || 'Not Assigned'}</p> {/* Adjust this based on how you're tracking user-specific statuses */}
          <button onClick={() => handleAssignProject(project.id)}>Assign to Me</button>
          <button onClick={() => handleStatusChange(project.id, 'in progress')}>Start Project</button>
          <button onClick={() => handleStatusChange(project.id, 'done')}>Mark as Done</button>
        </div>
      ))}
    </div>
  );
};

export default Projects;
