// App.js
import React from 'react';
import Navbar from './components/Navbar/Navbar';
import Sidebar from './components/Sidebar/Sidebar';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login/Login';
import Register from './components/Register/Register';
import Profile from './components/Profile/Profile';
import Projects from './components/Projects/Projects';
import Issues from './components/Issues/Issues';
import { AuthProvider } from './AuthContext'; // Adjust the path as needed to where your AuthContext is defined
import './App.scss';

function App() {
  return (
    <AuthProvider> {/* Wrap your Router and components within AuthProvider */}
      <Router>
        <Navbar />
        <div className="app-body">
          <Sidebar />
          <div className="content">
            <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              {/* Protected routes will now check authentication status from context */}
              <Route path="/profile/:userId" element={<Profile />} />
              <Route path="/projects" element={<Projects />} />
              <Route path="/issues" element={<Issues />} />
              {/* Redirect based on authentication status will be handled in each component */}
            </Routes>
          </div>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
