//Projects.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Projects.scss';

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState({
    title: '',
    description: '',
    status: 'open',
    user_ids: [], // Adjust to manage multiple user IDs
  });

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/projects');
      setProjects(response.data);
    } catch (error) {
      console.error("Failed to fetch projects", error);
    }
  };

  const handleAddProject = async () => {
    try {
      // Adjust to send an array of user IDs
      const response = await axios.post('http://localhost:5000/api/projects', newProject);
      console.log('Project added:', response.data);
      fetchProjects(); // Refetch projects to display the newly added one
      setNewProject({ title: '', description: '', status: 'open', user_ids: [] }); // Reset form
    } catch (error) {
      console.error("Failed to add project", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === "user_ids") {
      // Example of handling multiple select inputs for user_ids
      // Adjust this according to your UI for selecting multiple users
      const options = e.target.options;
      const user_ids = [];
      for (let i = 0, l = options.length; i < l; i++) {
        if (options[i].selected) {
          user_ids.push(options[i].value);
        }
      }
      setNewProject(prev => ({ ...prev, user_ids }));
    } else {
      setNewProject(prev => ({ ...prev, [name]: value }));
    }
  };

  // This function now needs to be adjusted to work with the new multi-user assignment logic
  // Depending on how you want to implement user assignments in your UI, you might need to significantly change this logic
  const handleAssignToUser = async (projectId, user_ids) => {
    try {
      // Example call; adjust according to your actual implementation needs
      await axios.put(`http://localhost:5000/api/projects/${projectId}`, {
        user_ids // Assuming your API now expects an array of user IDs for assignments
      });
      fetchProjects(); // Refetch projects to update the assignment
    } catch (error) {
      console.error("Failed to assign project to user", error);
    }
  };

  const handleStatusChange = async (projectId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/projects/${projectId}`, {
        status: newStatus
      });
      fetchProjects(); // Refetch projects to update the status
    } catch (error) {
      console.error("Failed to change project status", error);
    }
  };

  return (
    <div className="projects-container">
      <h1>Projects</h1>
      <div className="add-project-form">
        <input
          name="title"
          type="text"
          placeholder="Title"
          value={newProject.title}
          onChange={handleInputChange}
        />
        <textarea
          name="description"
          placeholder="Description"
          value={newProject.description}
          onChange={handleInputChange}
        />
        <select name="status" value={newProject.status} onChange={handleInputChange}>
          <option value="open">Open</option>
          <option value="in progress">In Progress</option>
          <option value="done">Done</option>
        </select>
        <button onClick={handleAddProject}>Add Project</button>
      </div>
      {projects.map((project) => (
        <div key={project.id} className="project-item">
          <h2>{project.title}</h2>
          <p>{project.description}</p>
          <p>Status:
            <select onChange={(e) => handleStatusChange(project.id, e.target.value)} value={project.status}>
              <option value="open">Open</option>
              <option value="in progress">In Progress</option>
              <option value="done">Done</option>
            </select>
          </p>
          <p>Assigned to: {project.username ? project.username : 'None'}</p>
          <button onClick={() => handleAssignToUser(project.id)}>Assign to Me</button>
        </div>
      ))}
    </div>
  );
};

export default Projects;
